DROP PROCEDURE IF EXISTS sp_getLevelRate$$
CREATE DEFINER = 'root'@'localhost'
PROCEDURE sp_getLevelRate(IN _SystemId INT, IN _AffiliateSettingId INT, IN _PlayerId INT)
  COMMENT 'get affiliate level rate (scope: system>affiliatesetting>player)'
BEGIN
  DECLARE $MaxLevel INT;
  DECLARE $is_true INT;

  START TRANSACTION;

  SET $MaxLevel = 3;
  SET $is_true = 0;
  
  #DROP TEMPORARY TABLE IF EXISTS tmpLevelRate;  #create table if not exists tb2 select * from book;  
  CREATE TEMPORARY TABLE IF NOT EXISTS tmpLevelRate ( `level` int, rate DECIMAL(16, 2));
  TRUNCATE TABLE tmpLevelRate;

  IF _PlayerId IS NOT NULL THEN   
    IF EXISTS(SELECT id FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='player' AND object_id=_PlayerId) THEN
      INSERT INTO tmpLevelRate SELECT `level`, rate FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='player' AND object_id=_PlayerId;
      SET $is_true = 1;
    END IF;   
  END IF;

  IF $is_true = 0 AND (_AffiliateSettingId IS NOT NULL) THEN
    IF EXISTS(SELECT id FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='setting' AND object_id=_AffiliateSettingId) THEN
      INSERT INTO tmpLevelRate SELECT `level`, rate FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='setting' AND object_id=_AffiliateSettingId;
      SET $is_true = 1;
    END IF;
  END IF;

  IF $is_true = 0 AND (_SystemId IS NOT NULL) THEN
    IF EXISTS(SELECT id FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='system') THEN
      INSERT INTO tmpLevelRate SELECT `level`, rate FROM affiliate_level_rate WHERE system_id=_SystemId AND scope='system';
      SET $is_true = 1;
    END IF;
  END IF;

  IF $is_true = 0 THEN
    INSERT INTO tmpLevelRate SELECT `level`, rate FROM affiliate_level_rate WHERE system_id=0 AND scope='system';
    SET $is_true = 1;
  END IF;

  SELECT * FROM tmpLevelRate;
  
  COMMIT;

END
$$

