CREATE DEFINER = 'root'@'localhost'
PROCEDURE sp_getPlayerPartners(IN _SystemId INT, IN _PlayerId INT, IN _Level INT, IN _IsExPlayerId INT)
  COMMENT 'get player affiliate partners'
BEGIN
  DECLARE $MaxLevel INT;
  DECLARE $PlayerId INT;
  DECLARE $Level INT;

  START TRANSACTION;

  IF _Level = 0 or _Level > 3 THEN
    SET $MaxLevel = 3;
  ELSE
    SET $MaxLevel = _Level;
  END IF;

  IF _IsExPlayerId = 1 THEN
    #get player id by external_Player_id
    SELECT id INTO $PlayerId FROM players where system_id = _SystemId and external_Player_id = _PlayerId;
  ELSE
    SET $PlayerId = _PlayerId;
  END IF;

  #select $PlayerId as SystemPlayerId, $MaxLevel as MaxLevel;
  
  DROP TEMPORARY TABLE IF EXISTS tmpPartners;
  CREATE TEMPORARY TABLE tmpPartners 
    SELECT 1 as `level`, id, partner_id, external_Player_id, username, first_name, last_name, affiliate_setting_id, affiliate_code, `status` FROM players where 1<>1;

  DROP TEMPORARY TABLE IF EXISTS tmpPlayerId;
  CREATE TEMPORARY TABLE tmpPlayerId ( `level` int, id int);


  SET $Level = 1;

LOOP_LABEL: BEGIN

  WHILE $Level <= $MaxLevel DO

    IF $Level = 1 THEN
      INSERT INTO tmpPartners SELECT $Level AS `level`, id, partner_id, external_Player_id, username, first_name, last_name, affiliate_setting_id, affiliate_code, `status` 
        FROM players WHERE partner_id = $PlayerId;
    ELSE
      INSERT INTO tmpPartners SELECT $Level AS `level`, id, partner_id, external_Player_id, username, first_name, last_name, affiliate_setting_id, affiliate_code, `status` 
        FROM players WHERE partner_id IN (SELECT id FROM tmpPlayerId WHERE `level` = $Level - 1);
    END IF;

    IF EXISTS(SELECT id FROM tmpPartners WHERE `level`=$Level) THEN
      INSERT INTO tmpPlayerId SELECT `level`, id FROM tmpPartners WHERE `level`=$Level;
    ELSE
      LEAVE LOOP_LABEL;
    END IF;

    SET $Level = $Level + 1;

  END WHILE;

END LOOP_LABEL;


  IF _Level = 0 THEN
    SELECT * FROM tmpPartners ORDER BY `level`;
  ELSE
    SELECT * FROM tmpPartners WHERE `level` <= _Level ORDER BY `level`;
  END IF;
  
  COMMIT;

END
$$

