--
DROP PROCEDURE IF EXISTS sp_getPrevPeriod$$
CREATE DEFINER = 'root'@'localhost'
PROCEDURE sp_getPrevPeriod(IN i_period_type varchar(10), OUT o_start_timestamp int, OUT o_end_timestamp int)
BEGIN

  DECLARE start_time datetime;
  DECLARE end_time datetime;

  IF i_period_type = 'monthly' THEN

    SET start_time = DATE_SUB(CURDATE(), INTERVAL 1 MONTH);
    SET start_time = DATE_SUB(start_time, INTERVAL DAY(start_time) - 1 DAY);
    SET end_time = DATE_SUB(CURDATE(), INTERVAL DAY(CURDATE()) - 1 DAY);
    SET end_time = DATE_SUB(end_time, INTERVAL 1 SECOND);

  ELSEIF i_period_type = 'weekly' THEN

    SET start_time = DATE_SUB(CURDATE(), INTERVAL 1 WEEK);
    SET start_time = DATE_SUB(start_time, INTERVAL WEEKDAY(start_time) DAY);
    SET end_time = DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) DAY);
    SET end_time = DATE_SUB(end_time, INTERVAL 1 SECOND);

  ELSEIF i_period_type = 'daily' THEN

    SET start_time = DATE_SUB(CURDATE(), INTERVAL 1 DAY);
    SET end_time = DATE_SUB(start_time, INTERVAL -1 DAY);
    SET end_time = DATE_SUB(end_time, INTERVAL 1 SECOND);

  ELSEIF i_period_type = 'yearly' THEN
    SET start_time = CONCAT(YEAR(NOW()) - 1, '-01-01');
    SET end_time = CONCAT(YEAR(NOW()) - 1, '-12-31');

  END IF;

  SET o_start_timestamp = UNIX_TIMESTAMP(start_time);
  SET o_end_timestamp = UNIX_TIMESTAMP(end_time);

END
$$

