--
DROP PROCEDURE IF EXISTS sp_getRanking$$
CREATE DEFINER = 'root'@'localhost'
PROCEDURE sp_getRanking(IN _system_id BIGINT, IN _player_id INT, IN _top INT)
BEGIN

  IF _player_id IS NULL THEN #top system

    SELECT p.*, IFNULL(r.total_reward,0) AS total_reward FROM
      (
        SELECT id, external_player_id, username, first_name, last_name FROM players p
        WHERE id IN ( SELECT DISTINCT partner_id FROM players WHERE system_id=_system_id AND `status`='active' AND partner_id IS NOT NULL AND partner_id > 0)
      ) AS p
      LEFT JOIN 
      (
        SELECT player_id, SUM(reward) AS total_reward FROM player_rewards WHERE system_id=_system_id AND status='active' GROUP BY player_id
      ) AS r
      ON p.id = r.player_id      
      ORDER BY p.id, total_reward DESC
      LIMIT _top;

  ELSE #top player in system
    SELECT p.*, IFNULL(r.total_reward,0) AS total_reward FROM
      (
        SELECT p.id, p.external_player_id, p.username, p.first_name, p.last_name FROM players p WHERE p.partner_id = _player_id
      ) AS p
      LEFT JOIN 
      (
        SELECT player_id, SUM(reward) AS total_reward FROM player_rewards WHERE system_id=_system_id AND status='active' AND player_id IN (SELECT id FROM players WHERE partner_id=_player_id) GROUP by player_id
      ) AS r
      ON p.id = r.player_id
      ORDER BY id, total_reward DESC
      LIMIT _top;

  END IF;

END
