--
-- Definition for procedure sp_Pagination
--
DROP PROCEDURE IF EXISTS sp_Pagination$$
CREATE DEFINER = 'root'@'localhost'
PROCEDURE sp_Pagination(IN _PageNumber int, IN _PageSize int, IN _Table varchar(64), IN _Fields varchar(512), IN _Where varchar(1000), IN _OrderBy varchar(128))
  COMMENT 'paging a single table query'
BEGIN

  if _PageSize<=1 then
    set _PageSize=25;
  end if;

  if _PageNumber < 1 then
    set _PageNumber=1;
  end if;

  set @strsql = concat('SELECT ', _Fields,' FROM ', _Table, ' ', _Where, ' ', _OrderBy, ' LIMIT ', _PageNumber * _PageSize - _PageSize , ',', _PageSize);
  prepare stmtsql from @strsql; 
  execute stmtsql; 
  deallocate prepare stmtsql;

  set @strsqlcnt = concat('SELECT COUNT(*) AS TOTAL_ROWS, CEIL(COUNT(*)/', _PageSize, ') AS TOTAL_PAGES FROM ', _Table, ' ', _where);
  prepare stmtsqlcount from @strsqlcnt;
  execute stmtsqlcount;
  deallocate prepare stmtsqlcount;
 
END
$$

